import styled from 'styled-components';
// import { v4 } from 'uuid';

function MessageForm(){

  let _message;
  _message = null;
  function handleNewMessageFormSubmission(event)
  {
    event.preventDefault();
    let apiUrl = '/default/SendMessage?message='+encodeURIComponent(_message.value);

    fetch(apiUrl,{
      method: 'post',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/json',
      },
    });
    // console.log(_message.value);
    _message.value = '';
  }

  return (
    <Main>
      <form className="row" onSubmit={handleNewMessageFormSubmission}>
        <textarea placeholder='New Message'
          className="col-md-9"
          id='issue'
          ref={(textarea) => {_message = textarea;}}/>
        <div className='col-md-3'>
          <button className='btn btn-submit' type="submit">Submit</button>
        </div>
      </form>
    </Main>
  );
}

